import {AppBar, Button, Toolbar, Typography} from '@material-ui/core'
import {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css'
function App() {
  const DisplayAnime = ({anime}) => {
    const displayAnime = () => {
      return anime.map(data => {
        return (
          <div>
            <p>{data.anime}</p>
            <p>{data.character}</p>
            <p>{data.quote}</p>
          </div>
        )
      })
    }
    return <div>{displayAnime()}</div>
  }

  const AnimeQuotes = () => {
    const [quote, setQuote] = useState('')
    const [anime, setAnime] = useState([])
    const handleClick = e => {
      e.preventDefault
      window.location.reload()
    }
    const pagination = () => {
      axios
        .get(
          'https://animechan.vercel.app/api/quotes/anime?title=haikyuu&?page=1',
          {
            headers: {
              'Access-Control-Allow-Origin': '*',
              'Access-Control-Allow-Headers': 'Content-Type',
            },
          },
        )
        .then(response => {
          setAnime(response.data)
          console.log(response.data)
        })
    }
    const randomQuotes = () => {
      axios
        .get('https://animechan.vercel.app/api/random', {
          headers: {
            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Headers': 'Content-Type',
          },
        })
        .then(response => {
          setQuote(response.data)
          console.log(response.data)
        })
    }

    useEffect(() => {
      randomQuotes()
    }, [])
    return (
      <div>
        <AppBar position="static">
          <Toolbar>
            <Typography color="inherit">Anime Random quote</Typography>
          </Toolbar>
        </AppBar>
        <div>
          <p>{quote.quote}</p>
          {/* <DisplayAnime anime={anime} /> */}
        </div>
        <div style={{marginTop: '15px'}}></div>
        <Button
          variant="contained"
          color="primary"
          size="large"
          onClick={handleClick()}
        >
          Get Random Quote
        </Button>
      </div>
    )
  }
  return (
    <div className="App">
      <AnimeQuotes />
    </div>
  )
}

export default App
